mapper.update:
update N_Comment
set content = #{content}
where nc_num = #{nc_num}

mapper.select:
select nc_num,
content, to_char(regdate,'yyyy-mm-dd') regdate, id, bbsno,
r
	FROM(
	select nc_num, content, regdate, id, bbsno, rownum r
		FROM(
		select nc_num, content, regdate, id, bbsno
		from N_Comment
		where bbsno = #{bbsno}
		order by nc_num DESC
		)
		
)WHERE r >= #{sno} and r <= #{eno}
